class PsMobileAppAmounts extends BasePsMobileAppAmounts {

	public function getDuration() {

		$now = date_create ();
		$expiration_date = date_create ( $this->getExpirationDateAt () );
		$diff = date_diff ( $expiration_date, $now );
		return $diff->format ( '%a' );
	}

	public function getLastHistoryId() {

		$history_id = Doctrine::getTable ( 'PsHistoryMobileAppAmounts' )->getLastPayByUserId ( $this->getUserId () );
		return $history_id;
	}
}