<?php

/**
 * PsSymbolTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class PsSymbolTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object PsSymbolTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PsSymbol');
    }
    public function doSelectQuery(Doctrine_Query $query)
      {

        $a = $query->getRootAlias();

        $query->select (
            $a . '.*, ' .
            'c.school_name AS school_name, ' .
            'w.title as title_workplace,' .
            's.title as title_service,' .
            'CONCAT(u.first_name, " ", u.last_name) AS updated_by' );

            $query->leftJoin ( $a . '.UserUpdated u' );
            $query->leftJoin ( $a . '.PsCustomer c');
            $query->leftJoin ( $a . '.PsWorkPlaces w');
            $query->leftJoin ( $a . '.Service s');

         return $query;
      }
}